sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var menuFiltroObj = {};
    var menuFiltroJson = [];
    var menuFiltroInicial = [];

    //JSON.stringify(request.body.data.menu)

    helpers.snHttp('/api/x_issas_gesti_n_0/gestion_oportunidades_api/putMenuFiltrosOferta', {
            method: 'PUT',
            body: {
                menu: api.state.ofertasMenuFiltrosInicial
            },
            headers: {
                "Content-Type": "application/json"
            }
        })
        .then(({
            response
        }) => {
            menuFiltroObj = response.result[0];
            menuFiltroJson = [menuFiltroObj];
            api.setState('ofertasMenuFiltros', menuFiltroJson);
        })
        .catch(({
            error
        }) => {
            const message = `Error: ${error.data.error.message}`;
            console.error(message);
            api.emit('NOW_UXF_PAGE#ADD_NOTIFICATIONS', {
                id: 'alert5',
                status: 'high',
                icon: 'info-circle-outline',
                content: message,
                action: {
                    type: 'dismiss'
                }
            });
        });
}
