sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    //debugger;
    try {
        const {
            context,
            data,
            state,
            setState,
            emit
        } = api;
        //     let itemCliente;
        //     if(event.payload.item.tabla='cliente'){
        // itemCliente+=itemCliente+event.payload.item.id;
        //     }
        const {
            payload
        } = event;

        let queryURL = "";

        var idsAreas = "";
        var idsClientes = "";
        var idsMercados = "";
        var idsEstados = "";
        var idsPaises = "";

        const {
            getFiltro
        } = imports['x_issas_gesti_n_0.ObtenerFiltrosInclude']();


        if (payload) {
            console.log("payload", payload);
            idsAreas = getFiltro(payload, 'area_funcional');
            console.log("idsAreas", idsAreas);
            queryURL += idsAreas !== "" ? "area_funcional.sys_idIN" + idsAreas + "^" : "";
            idsClientes = getFiltro(payload, 'cliente');
            queryURL += idsClientes !== "" ? "cliente.sys_idIN" + idsClientes + "^" : "";
            idsMercados = getFiltro(payload, 'mercado');
            queryURL += idsMercados !== "" ? "mercado.sys_idIN" + idsMercados + "^" : "";
            idsEstados = getFiltro(payload, 'estado');
            queryURL += idsEstados !== "" ? "estado.IN" + idsEstados + "^" : "";
            idsPaises = getFiltro(payload, 'pais');
            queryURL += idsPaises !== "" ? "pais.sys_idIN" + idsPaises + "" : "";
        }

        //cliente=8dcc07ecdb1a0510f7265eea4b961995^area_funcional=e67072a4dbd60510f7265eea4b961930,8d8072a4dbd60510f7265eea4b9619a8
        if (queryURL) {
            console.log('queryURL', queryURL);
            setState('ofertasPayload', queryURL);
        } else {
            setState('ofertasPayload', '');
        }
    } catch (e) {
        debugger;
    }
}
