sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
	if (!previous.oferta.nil() && previous.oferta !== current.oferta) {
		//actualizas la anterior oferta
		adjuntoReferencia(previous.oferta);
	}

	if (!current.oferta.nil()) {
		//actualizas la nueva oferta
		adjuntoReferencia(current.oferta);
    }

    function adjuntoReferencia(oferta) {

        var record = new GlideRecord('x_issas_gesti_n_0_oferta');
        record.addQuery('sys_id', oferta);
        record.query();
        while (record.next()) {

            var adjunto = new GlideRecord('x_issas_gesti_n_0_adjunto_referencia');
            adjunto.addQuery('oferta', record.sys_id.getDisplayValue());
            adjunto.query();
            if (adjunto.next()) {
                record.adjunto.setDisplayValue('1');
                record.update();

            } else {

                record.adjunto.setDisplayValue('0');
                record.update();
            }

        }

    }
})(current, previous);
