sys_script_include.script:
var ObtenerFiltrosContentTreeComponent = Class.create();
ObtenerFiltrosContentTreeComponent.prototype = {
	getTipoPrimerFiltro: function(payload) {
		for (var i = 0; i < payload.value.length; i++) {
			return payload.value[i][1];
		}
		return "";
	},
	getFiltrosByTipo: function(payload, tipo) {
		var idsCliente = "";
		var inicio = false;
		for (var i = 0; i < payload.value.length; i++) {
			if (payload.value[i][1] == tipo) {
				if (payload.value[i][2]) {
					if (inicio == true) {
						idsCliente += ",";
					}
					idsCliente += payload.value[i][2];
					inicio = true;
				}
			}
		}
		return idsCliente;
	}
};
