sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {
    var menuFiltroObj = {};
    var menuFiltro = [];

    var payload = [];
    var queryURL = [];
    var defaultFiltros = "";

    defaultFiltros = "anio_oferta.IN2019^";
    queryURL += defaultFiltros !== "" ? defaultFiltros : "";

    helpers.snHttp('/api/x_issas_gesti_n_0/gestion_oportunidades_api/getMenuFiltrosOferta', {
            method: 'GET',
            body: {
                filtros: payload
            },
            headers: {
                "Content-Type": "application/json"
            }
        })
        .then(({
            response
        }) => {
            menuFiltroObj = response.result[0];
            menuFiltro = [menuFiltroObj];
            api.setState('ofertasMenuFiltros', menuFiltro);
            console.log('menuFiltro', menuFiltro);
        })
        .catch(({
            error
        }) => {
            const message = `Error: ${error.data.error.message}`;
            console.error(message);
            api.emit('NOW_UXF_PAGE#ADD_NOTIFICATIONS', {
                id: 'alert5',
                status: 'high',
                icon: 'info-circle-outline',
                content: message,
                action: {
                    type: 'dismiss'
                }
            });
        });
}
