sys_ux_client_script_include.script:
function include() {
	return {
		getFiltro:function(payload,tipo){
			var idsCliente = "";
            var inicio = false;
			const result = payload.value.filter(x => x).forEach(function(y) {
				if (y[1] == tipo) {
                    if (y[2]) {
                        if (inicio == true) {
                            idsCliente += ",";
                        } 
                        idsCliente += y[2];
                        inicio = true;
                    }
                }
			});
			  return idsCliente;
		},

	};
}
